// export default Pagination;

export default function Pagination({
  tamaño,
  pokemonPerPage,
  pageactual,
}) {
  const dispach = useDispatch();
  const pageNumbers = [];
  for (let i = 0; i < Math.ceil(tamaño / pokemonPerPage); i++) {
    pageNumbers.push(i + 1);
  }

  function handelClickpage(numero) {
    dispach(paginado(numero));
  }

  /* next */
  function handleClickNext() {
    if (pageactual < pageNumbers.length) {
      dispach(paginado(pageactual + 1));
    } else {
      alert('there are no more pages');
    }
  }
  function handleClickPreview() {
    if (pageactual > 1) {
      dispach(paginado(pageactual - 1));
    } else {
      alert('there are no more pages');
    }
  }

  return (
    <div className={styles.container__paginado}>
      <button className={styles.button__paginado} onClick={handleClickPreview}>
        <FaAngleDoubleLeft />
      </button>
      {pageNumbers.map((page) => {
        return (
          <button
            className={`${styles.button__paginado} ${
              page === pageactual ? styles.b__active : styles.button__paginado
            }`}
            key={page}
            onClick={() => handelClickpage(page)}
          >
            {page}
          </button>
        );
      })}
      <button className={styles.button__paginado} onClick={handleClickNext}>
        <FaAngleDoubleRight />
      </button>
    </div>
  );
}
