import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { cleanPokemons, getPokemons } from "../../actions";
import Card from "../Card/Card";
import Filters from "../filtros/filtros";
import Pagination from "../Pagination/Pagination";
import Nav from "../Nav/Nav";
import Loading from "../Loading/Loading";
import styles from "./Home.module.css";

export default function Home() {
  const dispatch = useDispatch();
  const allpokemons = useSelector((state) => state.pokemons);
  const page = useSelector((state) => state.page);
  console.log(allpokemons);
  /*-------------------------*/
  let currentPokemons = [];

  const tamaño = allpokemons.length;

  const pokemonPerPage = 12;

  let indexOfLastPokemon = pokemonPerPage * page; // 12 pagina

  let indexOfFirstPokemon = indexOfLastPokemon - pokemonPerPage; // 9-9=0

  currentPokemons = allpokemons.slice(indexOfFirstPokemon, indexOfLastPokemon);
  /*-------------------------*/
  const [currentPage, setCurrentPage] = useState(1);
  // const [pokemonPerPage, setPokemonsPerPage] = useState(12);
  const [order, setOrder] = useState("");
const [score, setScore] = useState("");
  // const indexOfLastPokemon = currentPage * pokemonPerPage;
  // const indexOfFirstPokemon = indexOfLastPokemon - pokemonPerPage;

  // const currentPokemons = allpokemons.slice(
  //   indexOfFirstPokemon,
  //   indexOfLastPokemon
  // );
  // console.log(currentPokemons);
 

  useEffect(() => {
    dispatch(getPokemons());
  }, [dispatch]);

  const handleClick = (e) => {
    e.preventDefault();
    dispatch(cleanPokemons(dispatch));
    dispatch(getPokemons());
  };

  return (
    <div className={styles.container}>
      {allpokemons.length > 0 ? (
        <div>
          <Nav />
          <div className={styles.home}>
            <div className={styles.filters}>
              <Filters setorder={setOrder} setscore={setScore} />
              <button
                className={styles.btn}
                onClick={(e) => {
                  handleClick(e);
                }}
              >
                Clear filters
              </button>
            </div>
            <div>
              <div>
                <Pagination
                  tamaño={tamaño}
                  pokemonPerPage={pokemonPerPage}
                  pageactual={page}
                />
              </div>
              <div className={styles.cards}>
                {currentPokemons &&
                  currentPokemons?.map((e, k) => {
                    return (
                      <div key={k} className={styles.card}>
                        <Card
                          key={e.id}
                          id={e.id}
                          name={e.name}
                          img={e.img}
                          types={e.types}
                        />
                      </div>
                    );
                  })}
              </div>
            </div>
          </div>
        </div>
      ) : (
        <Loading />
      )}
    </div>
  );
}
