import React from "react";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { trueLoader, cleanDetalles, getDetail } from "../../actions";
import { useEffect } from "react";
import NIA from "../../imagenes/NIA.png";
import Loading from "../Loading/Loading";
import styles from "../Detalles/Detalles.css";
import { capitalizeString } from "../U";

const Detalles = (props) => {
  // const dispatch = useDispatch();
  // const myPokemon = useSelector((state) => state.pokeDetalles);
  // console.log(myPokemon);
  // useEffect(() => {
  //   dispatch(getDetail(props.match.params.id));
  //   return () => {
  //     dispatch(cleanDetalles(dispatch), cleanPokemons(dispatch));
  //   };
  // }, [dispatch, props.match.params.id]);

  // return (
  //   <div>
  //     {myPokemon.length > 0 ? (
  //       <div className={styles.container}>
  //         <div className={styles.card}>
  //           <h2 className={styles.h2}>
  //             {myPokemon[0].name}
  //           </h2>
  //           <p className={styles.p}>#{myPokemon[0].id}</p>
  //           <img
  //             src={myPokemon[0].img }
  //             alt="img not found"
  //             height="250px"
  //             width="200px"
  //           />
  //           <div className={styles.types}>
  //             <h3>
  //               {myPokemon[0].types?.map((e, k) => {
  //                 return (
  //                   <div className={styles.types} key={k}>
  //                     <img className={styles.typesImg} src={e.img} alt="X" />
  //                     <p className={styles.text}>{e}</p>
  //                   </div>
  //                 );
  //               })}
  //             </h3>
  //           </div>
  //           <h5 className={styles.h5}>HP: {myPokemon[0].hp}</h5>
  //           <h5 className={styles.h5}>Attack: {myPokemon[0].attack}</h5>
  //           <h5 className={styles.h5}>Defense: {myPokemon[0].defense}</h5>
  //           <h5 className={styles.h5}>Speed: {myPokemon[0].speed}</h5>
  //           <h5 className={styles.h5}>Height: {myPokemon[0].height}</h5>
  //           <h5 className={styles.h5}>Weight: {myPokemon[0].weight}</h5>
  //         </div>
  //       </div>
  //     ) : (
  //       <div>
  //         <Loading />
  //       </div>
  //     )}
  //     <div>
  //       <Link to="/home">
  //         <button className={styles.btn}>Go back</button>
  //       </Link>
  //     </div>
  //   </div>
  // );

     const dispatch = useDispatch();
     //Estados globales de Redux
     const pokemon = useSelector((state) => state.pokeDetalles);
     const loader = useSelector((state) => state.loader);

     useEffect(() => {
       //componentWillMount
       dispatch(getDetail(props.match.params.id)); //al montar el componente dispara getDetails para cargar la información del pokemon en la ruta de detalles
       return () => {
         //componentWillUnmount
         dispatch(cleanDetalles());
         dispatch(trueLoader());
       };
       // eslint-disable-next-line react-hooks/exhaustive-deps
     }, []);

     return (
       <div className={styles.detailscontainer}>
         <Link className={styles.link} to="/home">
           <div className={styles.wikiimg}>
             <div>
               <img src={NIA} alt="not found" />
             </div>
             <p>Volver</p>
           </div>
         </Link>
         {/* Mientras el estado de detalles (que es un array) esté vació, se va a estar mostrando el Loader */}
         {pokemon.length > 0 ? (
           <div className={styles.details}>
             {pokemon[0].createInDb ? (
               <span className={styles.created}>Created</span>
             ) : (
               <span className={styles.created}># {pokemon[0].id}</span>
             )}
             <h1 className={styles.title}>
               {capitalizeString(pokemon[0].name)}
             </h1>
             <h3 className={styles.types}>
               {pokemon[0].types?.map((type) => {
                 return (
                   <p className={styles[type]} key={type}>
                     {capitalizeString(type)}
                   </p>
                 );
               })}
             </h3>
             <div className={styles.flexdata}>
               <div>
                 <img
                   className={styles.pokeimg}
                   src={pokemon[0].img ? pokemon[0].img : NIA}
                   alt="not found"
                 />
               </div>
               <div className={styles.dataskill}>
                 <p>
                   Vida: <span>{pokemon[0].hp}</span>
                 </p>
                 <p>
                   Fuerza: <span>{pokemon[0].strength}</span>
                 </p>
                 <p>
                   Defensa: <span>{pokemon[0].defense}</span>
                 </p>
                 <p>
                   Velocidad: <span>{pokemon[0].speed}</span>
                 </p>
                 <p>
                   Altura: <span>{pokemon[0].height}</span>
                 </p>
                 <p>
                   Peso: <span>{pokemon[0].weight}</span>
                 </p>
               </div>
             </div>
             {/* Mientras loader sea true y el estado de detalles esté vació se va a mostrar el loader */}
           </div>
         ) : loader ? (
           <Loading />
         ) : null}
         {/* Si loader cambia a false y sigue vacío el componente de detalles, quiere decir que la carga falló o el pokemon no se encontró, por lo que vamos a mostrar una imagen con un texto que diga que el pokemon no se encontró */}
         {loader ? null : (
           <div>
             <Link style={{ textDecoration: "none", color: "#fff" }} to="/home">
               <img
                 src={NIA}
                 alt="Pokemon not Found"
                 width="300px"
                 height="300px"
               />
               <h2> Pokemon Not Found</h2>
             </Link>
           </div>
         )}
         {/* <Footer /> */}
       </div>
     );
};

export default Detalles;
