import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { cleanPokemons, getPokemons } from '../../actions';
import Card from '../Card/Card';
import Filters from '../Filters/Filters';
import Pagination from '../Pagination/Pagination';
import Nav from '../Nav/Nav';
import Loading from '../Loading/Loading';
import styles from './Home.module.css'

export default function Home() {

    const dispatch = useDispatch();
    const allPokemons = useSelector((state) => state.pokemons)
    //Paginado acá abajo
    const [currentPage, setCurrentPage] = useState(1);
    const [pokemonsPerPage, setPokemonsPerPage] = useState(12);
    const [order, setOrder] = useState(''); //Para modificar el estado local y me ayude al renderizado
    const indexOfLastPokemon = currentPage * pokemonsPerPage;
    const indexOfFirstPokemon = indexOfLastPokemon - pokemonsPerPage;
    // console.log(allPokemons);
    const currentPokemons = allPokemons.slice(indexOfFirstPokemon, indexOfLastPokemon);

    const pagination = pageNumber => {
        setCurrentPage(pageNumber);
    }

    useEffect(() => {
        dispatch(getPokemons())
    }, [dispatch])

    const handleClick = (e) => {
        e.preventDefault();
        dispatch(cleanPokemons(dispatch));
        dispatch(getPokemons());
    }


    return(
        <div>
            { allPokemons.length > 0 ?
                <div>
                    <Nav />
                    <div className={styles.home}>
                        <div className={styles.filters}>
                            <Filters setCurrentPage={setCurrentPage} setOrder={setOrder} />
                            <button className={styles.btn} onClick={e => {handleClick(e)}}>Clear filters</button>
                        </div>
                        <div>
                            <div>
                                <Pagination
                                    pokemonsPerPage={pokemonsPerPage}
                                    allPokemons={allPokemons.length}
                                    pagination={pagination}
                                />
                            </div>
                            <div className={styles.cards}>
                            {
                                currentPokemons?.map((e, k) => {
                                    return(
                                        <div key={k} className={styles.card}>
                                            <Card 
                                            key={e.id}
                                            id={e.id}
                                            name={e.name}
                                            img={e.img}
                                            types={e.types} />
                                        </div>
                                    )
                                })
                            }
                            </div>
                        </div>
                    </div>
                </div> :
                <Loading />
            } 
        </div>
    )
};
