import { acciones } from "../actions";

const initialState = {
  pokemons: [],
  allPokemons: [],
  types: [],
  pokeDetalles: [],
};

const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case acciones.GET_POKEMONS:
      return {
        ...state,
        pokemons: action.payload,
        allPokemons: action.payload,
      };
    case acciones.CLEAN_POKEMONS:
      return {
        ...state,
        pokemons: action.payload,
      };

    case acciones.GET_ALL_TYPES:
      return {
        ...state,
        types: action.payload,
      };
    case acciones.FILTER_CREATED:
      let copia = state.allPokemons;
      let createFiltros;
      if (action.payload === "created") {
        createFiltros = copia.filter((e) => e.createInDb);
      } else if (action.payload === "api") {
        createFiltros = copia.filter((e) => !e.createInDb);
      } else {
        createFiltros = copia;
      }
      return {
        ...state,
        pokemons: createFiltros,
      };
    case acciones.FILTER_TYPE:
      let copia2 = state.pokemons;
      let typesFlit =
        action.payload === "all"
          ? copia2
          : copia2.filter((e) =>
              e.types.some((e) => e.name === action.payload)
            );
      if (typesFlit.length <= 0) {
        typesFlit = copia2;
        alert("no se encuentra el typo de pokemon indicado");
      }
      return {
        ...state,
        pokemons: typesFlit,
      };

    case acciones.FILTER_NAME:
      let copia3 = state.pokemons;
      let NameSrd =
        action.payload === "asc"
          ? copia3.sort((a, b) => {
              return a.name.toLowerCase().localeCompare(b.name.toLowerCase());
            })
          : copia3.sort((a, b) => {
              return b.name.toLowerCase().localeCompare(a.name.toLowerCase());
            });
      return {
        ...state,
        pokemons: NameSrd,
      };
    case acciones.FILTER_STR:
      let copia4 = state.pokemons;
      let SortedStr =
        action.payload === "asc"
          ? copia4.sort((a, b) => a.attack - b.attack)
          : copia4.sort((a, b) => b.attack - a.attack);
      return {
        ...state,
        pokemons: SortedStr,
      };
    case acciones.GET_POKEMON_NAME:
      return {
        ...state,
        pokemons: action.payload,
      };
    case acciones.GET_DETAILS:
      return {
        ...state,
        pokeDetalles: action.payload,
      };
    case acciones.CLEAN_DETAIL:
      return {
        ...state,
        pokeDetalles: action.payload,
      };
    case acciones.POST_POKEMON:
      return {
        ...state,
      };

    default:
      return { ...state };
  }
};
export default rootReducer;
