import { 
    GET_POKEMONS, 
    GET_ALL_TYPES, 
    FILTER_CREATED, 
    ORDER_NAME, 
    FILTER_TYPE,
    ORDER_STR,
    GET_POKEMON_NAME,
    POST_POKEMON,
    GET_DETAILS,
    CLEAN_DETAIL,
    CLEAN_POKEMONS
 } from "../actions";

const initialState = {
    pokemons: [],
    pokemonBackup:[],
    allPokemons: [],
    types: [],
   pokeDetail:  []
}

const rootReducer = (state = initialState, action) => {

    switch(action.type) {
        case GET_POKEMONS:
            return {
                ...state,
                pokemons: action.payload,
                allPokemons: action.payload
            };
        case CLEAN_POKEMONS:
            return {
                ...state,
                pokemons: action.payload
            }    
        case GET_ALL_TYPES:
            return {
                ...state,
                types: action.payload
            }; 
        case FILTER_CREATED:
            let copy = state.allPokemons;
            let createdFiltered;
            if (action.payload === 'created') {
                createdFiltered = copy.filter((e) => e.createInDb);
            } else if (action.payload === 'api') {
                createdFiltered = copy.filter((e) => !e.createInDb);
            } else {
                createdFiltered = copy;
            }
            return {
                ...state,
                pokemons: createdFiltered
            };
        case FILTER_TYPE:
            // let copyTwo = state.pokemonBackup;
            // let typeFiltered;

            //  if (action.payload === 'all'){
            //     typeFiltered=copyTwo
            //  } else{
            //     typeFiltered = copyTwo.filter((e) =>
            //       e.types?.includes(action.payload)
            //     );

            //  }

            // return {
            //     ...state,
            //     pokemons: typeFiltered
            // };
            const allVideoGames = state.pokemonBackup;
            const genresFiltered = action.payload === "Genres" ? allVideoGames : allVideoGames.filter(e => e.genres?.includes(action.payload))
            return{
                ...state,
                videogames: genresFiltered,
            }
        case ORDER_NAME:
            let copy3 = state.pokemons;
            let sortedName = action.payload === 'asc' ?
                copy3.sort((a, b) => {
                    return a.name.toLowerCase().localeCompare(b.name.toLowerCase())
                }) :
                copy3.sort((a, b) => {
                    return b.name.toLowerCase().localeCompare(a.name.toLowerCase())
                })
            return {
                ...state,
                pokemons: sortedName
            };          
        case ORDER_STR:
            let copy4 = state.pokemons;
            let sortedStr = action.payload === 'asc' ?
                copy4.sort((a, b) => a.attack - b.attack) :
                copy4.sort((a, b) => b.attack - a.attack);
            // console.table(sortedStr);    
            return {
                ...state,
                pokemons: sortedStr
            };
        case GET_POKEMON_NAME:
            return {
                ...state,
                pokemons: action.payload
            };
        case GET_DETAILS:
            return {
                ...state,
                pokeDetail: action.payload
            }
        case CLEAN_DETAIL:
            return {
                ...state,
                pokeDetail: action.payload
            }
        case POST_POKEMON:
            return {
                ...state
            };
        default: 
            return {...state};
    };

};

export default rootReducer;
