import axios from "axios";

export const acciones = {
  GET_POKEMONS: "GET_POKEMONS",
  GET_ALL_TYPES: "GET_ALL_TYPES",
  FILTER_CREATED: "FILTER_CREATED",
  FILTER_NAME: "ORDER_NAME",
  FILTER_TYPE: "FILTER_TYPE",
  FILTER_STR: "FILTER_STR",
  GET_POKEMON_NAME: "GET_POKEMON_NAME",
  POST_POKEMON: "POST_POKEMON",
  GET_DETAILS: "GET_DETAILS",
  CLEAN_DETAIL: "CLEAN_DETAIL",
  CLEAN_POKEMONS: "CLEAN_POKEMONS",
};

export const getPokemons = () => {
  return async (dispatch) => {
    try {
      let url = await axios.get("ttp://localhost:3001/pokemons");
      return dispatch({
        type: acciones.GET_POKEMONS,
        payload: url.data,
      });
    } catch (err) {
      console.log(err);
    }
  };
};

export const getAllTypes = () => {
  return async (dispatch) => {
    try {
      let url = await axios.get("http://localhost:3001/types");
      return dispatch({
        type: acciones.GET_ALL_TYPES,
        payload: url.data,
      });
    } catch (err) {
      console.log(err);
    }
  };
};

export const getPokemonByName = (name) => {
  return async (dispatch) => {
    try {
      let url = await axios.get(`http://localhost:3001/pokemons?name=${name}`);
      return dispatch({
        type: acciones.GET_POKEMON_NAME,
        payload: url.data,
      });
    } catch (err) {
      alert("Pokemon not found");
      window.location.href = "http://localhost:3000/home";
      console.log(err);
    }
  };
};

export function getDetail(id) {
  return async (dispatch) => {
    try {
      let url = await axios.get(`http://localhost:3001/pokemons/${id}`);
      return dispatch({
        type: acciones.GET_DETAILS,
        payload: url.data,
      });
    } catch (err) {
      console.log(err);
    }
  };
}

export const cleanPokemons = (dispatch) => {
  return dispatch({
    type: acciones.CLEAN_POKEMONS,
    payload: [],
  });
};

export const cleanDetalles = (dispatch) => {
  return dispatch({
    type: acciones.CLEAN_DETAIL,
    payload: [],
  });
};

export const filterCreated = (payload) => {
  return {
    type: acciones.FILTER_CREATED,
    payload,
  };
};

export const filterType = (payload) => {
  return {
    type: acciones.FILTER_TYPE,
    payload,
  };
};

export const filterStr = (payload) => {
  return {
    type: acciones.FILTER_STR,
    payload,
  };
};

export const filterName = (payload) => {
  return {
    type: acciones.FILTER_NAME,
  };
};

export const postPokemon = (payload) => {
  return async () => {
    try {
      let createPoke = await axios.post(
        "http://localhost:3001/pokemons",
        payload
      );
      console.log(createPoke);
      alert("New Pokemon is Created!");
      return createPoke;
    } catch (err) {
      alert("failed to create pokemon");
      console.log(err);
    }
  };
};
